--source: imdb.com

drop table if exists MOVIE;
drop table if exists CATEGORY;

create table CATEGORY
(
    id number(12) primary key,
    name varchar(30)
);


create table MOVIE
(id       number(12) primary key,
 title    varchar2(400) not null,
 category_id number(12) ,
 year     char(4),
 plot    varchar2(4000),
 price    number(5,2),
 foreign key (category_id) references CATEGORY(id));

